update.foreach:
    		UPDATE /* AdvnCmt.update 매일 점수 및 예정 등급은 업데이트 한다. */ CRM_CUST_BAS SET 
	    		MSHIP_EXPT_GRADE_CD = #{item.mshipExptGradeCd} 
	    		, MSHIP_ADVNCMT_SCORE = #{item.mshipAdvnCmtScore}
	    		
	    		     
	                    , MSHIP_GRADE_CD =  #{item.mshipExptGradeCd}
				
    		WHERE ITG_CUST_NO = #{item.itgCustNo}

mapper.update:
    
    		UPDATE /* AdvnCmt.updateOne 매일 점수 및 예정 등급은 업데이트 한다. */ CRM_CUST_BAS SET 
	    		MSHIP_EXPT_GRADE_CD = #{mshipExptGradeCd} 
	    		, MSHIP_ADVNCMT_SCORE = #{mshipAdvnCmtScore}
	    		
	    		     
	                    , MSHIP_GRADE_CD =  #{mshipExptGradeCd}
				
    		WHERE ITG_CUST_NO = #{itgCustNo}

insert.foreach:
		
			 INSERT /* AdvnCmt.insert */ INTO CRM_CUST_GRADE_CHNG_HST (
   				  GRADE_CHNG_HST_SEQ                    /*등급변경이력일련번호        */
                , ITG_CUST_NO                    /*통합고객번호        */
                , APPLY_STD_STA_YMD                    /*적용기준시작년월일        */
                , APPLY_STD_END_YMD                    /*적용기준종료년월일        */
                , MSHP_GRADE_CD                    /*회원등급코드        */
                , CHNG_PRE_MSHP_GRADE_CD                    /*변경전회원등급코드        */
                , MSHP_GRADE_TYPE_CD                    /*회원등급유형코드        */
                , AMDR_ID                    /*수정자ID        */
                , AMD_DT                    /*수정일시        */
                , REGR_ID                    /*등록자ID        */
                , REG_DT                    /*등록일시        */
                , REG_CHL_CD                    /*등록채널코드        공통코드 : S000        [CRM : CRM , CTC : 상담 , AS : AS , SAP : SAP , POS : POS]*/
             ) VALUES (
                   NVL(#{item.gradeChngHstSeq}, FN_CRM_AUTO_SEQ('GCH') ) 
                 , #{item.itgCustNo}
                 , #{item.applyStdStaYmd}
                 , #{item.applyStdEndYmd}
                 , #{item.mshipExptGradeCd}
                 , #{item.nowMshipGradeCd}
                 , #{item.mshipGradeCd}
                 , #{item.amdrId}
                 , SYSDATE
                 , #{item.regrId}
                 , SYSDATE
                 , #{item.regChlCd}
             )

