SELECT * FROM ( SELECT /*com.ceragem.batch.crm.dao.PromEventDao.getBosOrderAll*/ TEM.ITG_CUST_NO , TEM.SALE_TY_CD , TEM.SALE_ORGZ_CUST_CD , ST.STOR_NM , ST.STOR_CHL_CD , CASE WHEN TEM.SALE_TY_CD = '1401' OR TEM.SALE_TY_CD = '1402' OR TEM.SALE_TY_CD = '1403' OR TEM.SALE_TY_CD = '1404' THEN (TEM.SETL_AMT + TEM.RENT_AMT) * TEM.CNTR_PD_VAL WHEN TEM.SALE_TY_CD = '2101' OR TEM.SALE_TY_CD = '2102' OR TEM.SALE_TY_CD = '2103' OR TEM.SALE_TY_CD = '2104' OR TEM.SALE_TY_CD = '1202' THEN TEM.SETL_AMT WHEN TEM.SALE_TY_CD = '2401' THEN TEM.SETL_AMT END TOT_ORD_AMT , TEM.SETL_AMT , TEM.CNTR_PD_VAL , TEM.RENT_AMT , CUS.MSHIP_GRADE_CD , CUS.MSHIP_SBSC_YN , CUS.CPRT_CMP_NO , CUS.MSHIP_TYPE_CD FROM ( SELECT ITG_CUST_NO , SUM(SETL_AMT) AS SETL_AMT , SUM(ORD_AMT) AS ORD_AMT , SUM(CNTR_PD_VAL) AS CNTR_PD_VAL , SUM(RENT_AMT) AS RENT_AMT , SALE_TY_CD , SALE_ORGZ_CUST_CD FROM CRM_CUST_BOS_CNTRT_HST A JOIN CRM_BOS_GODS_BAS B ON A.ITEM_CD = B.BOS_GODS_NO WHERE IST_DCS_DE IS NOT NULL AND IST_DCS_DE >= #{PROM_STA_YMD} AND IST_DCS_DE <= #{PROM_END_YMD} AND SALE_ORGZ_CUST_CD IN (SELECT STOR_NO FROM CRM_MSHIP_APPLY_STOR_REL WHERE MSHIP_PROM_BAS_NO = #{MSHIP_PROM_BAS_NO}) AND BASE_ITEM_CD IN (SELECT GODS_NO FROM CRM_MSHIP_APPLY_GODS_REL WHERE MSHIP_PROM_BAS_NO = #{MSHIP_PROM_BAS_NO}) AND CNTR_STS_DTL_CD = '32' /*매출확정*/ AND SETL_STS_CD = '20' /*결제완료*/ AND IST_STS_CD = '50' /*설치확정*/ AND IST_DCS_YN = 'Y' /*설치확정여부*/ GROUP BY ITG_CUST_NO , SALE_TY_CD , SALE_ORGZ_CUST_CD , ORD_NO ORDER BY ITG_CUST_NO DESC ) TEM JOIN CRM_STOR_BAS ST ON TEM.SALE_ORGZ_CUST_CD = ST.STOR_NO JOIN CRM_CUST_BAS CUS ON TEM.ITG_CUST_NO = CUS.ITG_CUST_NO
OR CUS.MSHIP_TYPE_CD IN
#{item}
OR CUS.MSHIP_GRADE_CD IN
#{item}
OR CUS.CPRT_CMP_NO = #{cmpNo}
) TEM1 WHERE (
TEM1.TOT_ORD_AMT >= #{FROM_APPLY_SALE_AMT}
TEM1.TOT_ORD_AMT > #{FROM_APPLY_SALE_AMT}
AND TEM1.TOT_ORD_AMT <= #{TO_APPLY_SALE_AMT}
AND TEM1.TOT_ORD_AMT < #{TO_APPLY_SALE_AMT}
)

SELECT CUT.ITG_CUST_NO , CUT.CUST_NM , CUT.GNDR_CD , CUT.BIRTHDAY , RE.TOT_ORD_AMT , DAMO.DECRYPT_VAR_B64('AES_256',CUT.MPHON_NO,'') AS MPHON_NO FROM ( SELECT GRP.ITG_CUST_NO ,SUM(GRP.TOT_ORD_AMT) AS TOT_ORD_AMT FROM ( SELECT TEM.ITG_CUST_NO , CASE WHEN TEM.SALE_TY_CD = '1401' OR TEM.SALE_TY_CD = '1402' OR TEM.SALE_TY_CD = '1403' OR TEM.SALE_TY_CD = '1404' THEN (TEM.SETL_AMT + TEM.RENT_AMT) * TEM.CNTR_PD_VAL WHEN TEM.SALE_TY_CD = '2101' OR TEM.SALE_TY_CD = '2102' OR TEM.SALE_TY_CD = '2103' OR TEM.SALE_TY_CD = '2104' OR TEM.SALE_TY_CD = '1202' THEN TEM.SETL_AMT WHEN TEM.SALE_TY_CD = '2401' THEN TEM.SETL_AMT END TOT_ORD_AMT FROM ( SELECT ITG_CUST_NO , SUM(SETL_AMT) AS SETL_AMT , SUM(CNTR_PD_VAL) AS CNTR_PD_VAL , SUM(RENT_AMT) AS RENT_AMT , SALE_TY_CD , SALE_ORGZ_CUST_CD , ORD_NO FROM CRM_CUST_BOS_CNTRT_HST A WHERE IST_DCS_DE IS NOT NULL AND IST_DCS_DE >= #{PROM_STA_YMD} AND IST_DCS_DE <= #{PROM_END_YMD} AND SALE_ORGZ_CUST_CD IN (SELECT STOR_NO FROM CRM_MSHIP_APPLY_STOR_REL WHERE MSHIP_PROM_BAS_NO = #{MSHIP_PROM_BAS_NO}) AND BASE_ITEM_CD IN (SELECT GODS_NO FROM CRM_MSHIP_APPLY_GODS_REL WHERE MSHIP_PROM_BAS_NO = #{MSHIP_PROM_BAS_NO}) AND CNTR_STS_DTL_CD = '32' /*매출확정*/ AND SETL_STS_CD = '20' /*결제완료*/ AND IST_STS_CD = '50' /*설치확정*/ AND IST_DCS_YN = 'Y' /*설치확정여부*/ GROUP BY ITG_CUST_NO , SALE_TY_CD , SALE_ORGZ_CUST_CD , ORD_NO ORDER BY ITG_CUST_NO DESC )TEM
UNION ALL
SELECT TEM.ITG_CUST_NO , TEM.TOT_AMT AS TOT_ORD_AMT FROM ( SELECT A.SALE_DATE , A.STOR_NO , A.POS_NO , A.DEAL_NO , B.ITG_CUST_NO , SUM(A.TOT_SALE_AMT) AS TOT_AMT , SUM(A.DC_AMT) AS DC_AMT FROM CRM_CUST_POS_SALE_DTL A JOIN CRM_CUST_POS_SALE_HST B ON A.SALE_DATE = B.SALE_DATE AND A.STOR_NO = B.STOR_NO AND A.POS_NO = B.POS_NO AND A.DEAL_NO = B.DEAL_NO AND B.ITG_CUST_NO IS NOT NULL AND A.STOR_NO IN (SELECT STOR_NO FROM CRM_MSHIP_APPLY_STOR_REL WHERE MSHIP_PROM_BAS_NO = #{MSHIP_PROM_BAS_NO}) AND A.GODS_NO IN (SELECT GODS_NO FROM CRM_MSHIP_APPLY_GODS_REL WHERE MSHIP_PROM_BAS_NO = #{MSHIP_PROM_BAS_NO}) AND A.SALE_DATE >= #{PROM_STA_YMD} AND A.SALE_DATE <= #{PROM_END_YMD} AND A.DEAL_DIV_CD = 0 GROUP BY A.SALE_DATE , A.STOR_NO , A.POS_NO , A.DEAL_NO , B.ITG_CUST_NO ) TEM ) GRP GROUP BY GRP.ITG_CUST_NO ) RE JOIN CRM_CUST_BAS CUT ON RE.ITG_CUST_NO = CUT.ITG_CUST_NO AND CUT.CUST_STATUS_CD != '003'
AND RE.TOT_ORD_AMT >= #{FROM_APPLY_SALE_AMT}
AND RE.TOT_ORD_AMT > #{FROM_APPLY_SALE_AMT}
AND RE.TOT_ORD_AMT <= #{TO_APPLY_SALE_AMT}
AND RE.TOT_ORD_AMT < #{TO_APPLY_SALE_AMT}
OR CUT.MSHIP_TYPE_CD IN
#{item}
OR CUT.MSHIP_GRADE_CD IN
#{item}
OR CUT.CPRT_CMP_NO = #{cmpNo}

