insert.if:
INSERT ALL /* GrpCoupnIssue.pubCoupon */ INTO CRM_COUPN_PBLS_HST ( COUPN_PBLS_HST_SEQ , COUPN_PBLS_BAS_NO , ITG_CUST_NO , MSHIP_COUPN_BAS_NO , COUPN_KND_CD , COUPN_TGT_CD , COUPN_APPLY_DIV_CD1 , COUPN_APPLY_DIV_CD2 , FROM_PBLS_STD_DAY , TO_PBLS_STD_DAY , USE_STD_DAY_COND_CD , FROM_USE_STD_DAY , TO_USE_STD_DAY , GIFT_POSS_YN , COUPN_ISSUE_METH_CD , ISSUE_RSTRTN_CNT , MAX_ISSUE_CNT , MAX_USE_CNT , APPLY_AMT , APPLY_RATE , MIN_BUY_AMT , MAX_DC_AMT , APPLY_CNT , PRSNTTN_GODS_CD , USE_DOW , FROM_USE_HOUR , TO_USE_HOUR , USE_CHL_CD , DUP_USE_YN , USE_DIV_CD , USE_YN , COUPN_BAS_NM , COUPN_BAS_CTNTS , ADMT_METH_CD , ADMT_AMT , COUPN_TYPE_CD , COUPN_USE_POSS_DAY , COUPN_USE_POSS_YN , COUPN_USE_POSS_DAY_CNT , COUPN_CLASS_CD , DOW_1_USE_YN , DOW_2_USE_YN , DOW_3_USE_YN , DOW_4_USE_YN , DOW_5_USE_YN , DOW_6_USE_YN , DOW_7_USE_YN , MSHP_GRADE_CD , AMDR_ID , AMD_DT , REGR_ID , REG_DT , REG_CHL_CD , STOR_NO
)
SELECT /* GrpCoupnIssue.custNoList */ FN_CRM_AUTO_SEQ('CPN'), FN_LUHN_COUPN( DECODE(A.MSHIP_GRADE_CD, '001', '01', '002', '51', '003', '05', NULL, '05') ) COUPN_PBLS_BAS_NO , B.ITG_CUST_NO , A.MSHIP_COUPN_BAS_NO , COUPN_KND_CD , COUPN_TGT_CD , COUPN_APPLY_DIV_CD1 , COUPN_APPLY_DIV_CD2 , FROM_PBLS_STD_DAY , TO_PBLS_STD_DAY , USE_STD_DAY_COND_CD , DECODE(USE_STD_DAY_COND_CD, 'Y', TO_CHAR(SYSDATE, 'YYYYMMDD'), NULL, FROM_USE_STD_DAY ) , DECODE(USE_STD_DAY_COND_CD, 'Y', TO_CHAR( SYSDATE + NVL( COUPN_USE_POSS_DAY, 0) , 'YYYYMMDD'), NULL, TO_USE_STD_DAY ) , GIFT_POSS_YN , COUPN_ISSUE_METH_CD , ISSUE_RSTRTN_CNT , MAX_ISSUE_CNT , MAX_USE_CNT , APPLY_AMT , APPLY_RATE , MIN_BUY_AMT , MAX_DC_AMT , APPLY_CNT , PRSNTTN_GODS_CD , USE_DOW , FROM_USE_HOUR , TO_USE_HOUR , USE_CHL_CD , DUP_USE_YN , '002' , 'N' , COUPN_BAS_NM , COUPN_BAS_CTNTS , ADMT_METH_CD , ADMT_AMT , COUPN_TYPE_CD , COUPN_USE_POSS_DAY , COUPN_USE_POSS_YN , COUPN_USE_POSS_DAY_CNT , COUPN_CLASS_CD , DOW_1_USE_YN , DOW_2_USE_YN , DOW_3_USE_YN , DOW_4_USE_YN , DOW_5_USE_YN , DOW_6_USE_YN , DOW_7_USE_YN , A.MSHP_GRADE_CD , NULL AMDR_ID , NULL AMD_DT , #{regrId} , SYSDATE , NVL( A.REG_CHL_CD, 'CRM') REG_CHL_CD , '141359' FROM CRM_MSHIP_COUPN_BAS A
INNER JOIN CRM_CUST_BAS B ON B.CUST_STATUS_CD='001' AND B.MSHIP_SBSC_YN='Y'
AND B.MSHIP_TYPE_CD='001' AND B.MSHIP_TYPE_CD='002' AND B.CPRT_CMP_NO=#{cprtCmpNo} /* AND B.MSHIP_TYPE_CD='003' */ AND B.MSHIP_GRADE_CD=#{mshpGradeCd} AND 1 = 2
LEFT JOIN ( SELECT COUNT(1) PRV_QNTY , ITG_CUST_NO , MSHIP_COUPN_BAS_NO FROM CRM_COUPN_PBLS_HST WHERE MSHIP_COUPN_BAS_NO = #{mshipCoupnBasNo}
/* 1회 제공일 경우 체크 */
/* 매일 제공일 경우 체크 */ AND TO_CHAR(REG_DT, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD')
/* 매주 제공일 경우 체크 */ AND TO_CHAR(REG_DT, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD')
/* 매월 제공일 경우 체크 */ AND TO_CHAR(REG_DT, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD')
/* 분기별 제공일경우 체크 */ AND TO_CHAR(REG_DT, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD')
/* 매년 제공일 경우 체크 */ AND TO_CHAR(REG_DT, 'YYYY') = TO_CHAR(SYSDATE, 'YYYY')
GROUP BY ITG_CUST_NO, MSHIP_COUPN_BAS_NO
) C ON B.ITG_CUST_NO = C.ITG_CUST_NO INNER JOIN ( SELECT LEVEL LVL FROM DUAL CONNECT BY LEVEL <= #{prvQnty} ) D ON ( #{prvQnty}-NVL(PRV_QNTY,0) ) >= D.LVL
WHERE A.MSHIP_COUPN_BAS_NO = #{mshipCoupnBasNo} AND B.MSHIP_SBSC_YN='Y' AND A.USE_YN='Y' AND B.ITG_CUST_NO IS NOT NULL
